SELECT (str(?pidValue) as ?pid) (str(?wasGeneratedByValue) as ?wasGeneratedBy) FROM <$GRAPH_NAME> WHERE {
?result prov:wasGeneratedBy ?activity . ?result dcterms:identifier ?pidValue . ?activity dcterms:identifier ?wasGeneratedByValue .
}

